import React, { useState } from 'react';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    try {
      // Test if backend is reachable
      const test = await fetch('http://localhost:8000/');
      console.log('Backend test:', await test.json());

      // Try the analyze endpoint
      const formData = new FormData();
      formData.append('query', query);

      const res = await fetch('http://localhost:8000/analyze', {
        method: 'POST',
        body: formData
      });

      const data = await res.json();
      setResponse(data);
    } catch (error) {
      console.error('Error:', error);
      setResponse({
        error: true,
        message: `Failed to connect: ${error.message}`,
        details: 'Check if backend is running on port 8000'
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1>ðŸ¤– AI Document Assistant</h1>
      <p>Test Interface - Backend should be running on port 8000</p>

      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="What do you need help with?"
          style={styles.input}
        />
        <button type="submit" style={styles.button} disabled={loading}>
          {loading ? 'Checking...' : 'Ask AI'}
        </button>
      </form>

      {response && (
        <div style={styles.response}>
          <h3>Response:</h3>
          <pre style={styles.pre}>
            {JSON.stringify(response, null, 2)}
          </pre>
        </div>
      )}

      <div style={styles.info}>
        <h4>Debug Info:</h4>
        <p>â€¢ Check browser Console (F12) for errors</p>
        <p>â€¢ Backend should be at: http://localhost:8000</p>
        <p>â€¢ Test backend: <a href="http://localhost:8000" target="_blank">Click here</a></p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '40px',
    maxWidth: '800px',
    margin: '0 auto'
  },
  form: {
    margin: '30px 0'
  },
  input: {
    width: '100%',
    padding: '15px',
    fontSize: '16px',
    border: '2px solid #ccc',
    borderRadius: '8px',
    marginBottom: '15px'
  },
  button: {
    background: '#4CAF50',
    color: 'white',
    border: 'none',
    padding: '15px 30px',
    fontSize: '16px',
    borderRadius: '8px',
    cursor: 'pointer',
    width: '100%'
  },
  response: {
    background: '#f5f5f5',
    padding: '20px',
    borderRadius: '8px',
    marginTop: '30px'
  },
  pre: {
    background: '#333',
    color: '#fff',
    padding: '15px',
    borderRadius: '5px',
    overflow: 'auto',
    fontSize: '14px'
  },
  info: {
    background: '#e3f2fd',
    padding: '15px',
    borderRadius: '8px',
    marginTop: '20px'
  }
};

export default App;
